import 'mocha';
import {expect} from 'chai';
import {management} from '../src/management';
import {note} from '../src/note';

describe('Management', () => {
  let note1: note;
  let note2: note;
  let manager: management;

  beforeEach(() => {
    note1 = new note('user1', 'title1', 'body1', 'red');
    note2 = new note('user2', 'title2', 'body2', 'green');
    manager = new management();
  });

  it('should be a Management', () => {
    expect(manager).to.be.an.instanceof(management);
  });

  it('should have an addNote method', () => {
    expect(manager.addNote).to.be.a('function');
    expect(manager.addNote(note1, (err) => {
      expect(err).to.be.null;
    }));
    expect(manager.addNote(note2, (err) => {
      expect(err).to.be.null;
    }));
  });

  it('should have an exists method', () => {
    expect(manager.exists).to.be.a('function');
    expect(manager.exists(note1.title, note1.user)).to.be.true;
    expect(manager.exists(note2.title, note2.user)).to.be.true;
  });

  it('should have a getNotes method', () => {
    expect(manager.getNotes).to.be.a('function');
    expect(manager.getNotes(note1.user, (err, notes) => {
      expect(err).to.be.null;
      expect(notes).to.be.an('array');
      expect(notes.length).to.equal(1);
      expect(notes[0].user).to.equal(note1.user);
      expect(notes[0].title).to.equal(note1.title);
      expect(notes[0].body).to.equal(note1.body);
      expect(notes[0].color).to.equal(note1.color);
    }));
    expect(manager.getNotes(note2.user, (err, notes) => {
      expect(err).to.be.null;
      expect(notes).to.be.an('array');
      expect(notes.length).to.equal(1);
      expect(notes[0].user).to.equal(note2.user);
      expect(notes[0].title).to.equal(note2.title);
      expect(notes[0].body).to.equal(note2.body);
      expect(notes[0].color).to.equal(note2.color);
    }));
  });

  it('should have a getNote method', () => {
    expect(manager.getNote).to.be.a('function');
    expect(manager.getNote(note1.user, note1.title, (err, noteTmp) => {
      expect(err).to.be.null;
      const tmp = noteTmp as note;
      expect(tmp.user).to.equal(note1.user);
      expect(tmp.title).to.equal(note1.title);
      expect(tmp.body).to.equal(note1.body);
      expect(tmp.color).to.equal(note1.color);
    }));
    expect(manager.getNote(note2.user, note2.title, (err, noteTmp) => {
      expect(err).to.be.null;
      const tmp = noteTmp as note;
      expect(tmp.user).to.equal(note2.user);
      expect(tmp.title).to.equal(note2.title);
      expect(tmp.body).to.equal(note2.body);
      expect(tmp.color).to.equal(note2.color);
    }));
  });

  it('should have a removeNote method', () => {
    expect(manager.removeNote).to.be.a('function');
    expect(manager.removeNote(note1.user, note1.title, (err) => {
      expect(err).to.be.null;
    }));
    expect(manager.removeNote(note2.user, note2.title, (err) => {
      expect(err).to.be.null;
    }));
  });
});

import * as fs from 'fs';
import {note} from './note';

/**
 * Dirección por defecto de la base de datos
 * @type {string}
 * @constant
 */
const DIR = './src/db';

/**
 * Clase que gestiona las notas
 * @class
 */
export class management {
  private dir: string;

  /**
   * Constructor de la clase
   * @param dir Dirección de la base de datos
   */
  constructor(dir: string = DIR) {
    this.dir = dir;
  }

  /**
   * Función que comprueba si una nota existe
   * @param title Título de la nota
   * @param user Usuario de la nota
   * @returns {boolean}
   */
  public exists(title: string, user: string): boolean {
    const dir = this.dir + '/' + user;
    if (!fs.existsSync(dir)) {
      return false;
    }
    const files = fs.readdirSync(dir, { withFileTypes: true });
    for (const file of files) {
      if (file.isFile()) {
        if (file.name === title + '.json') {
          return true;
        }
      }
    }
    return false;
  }

  /**
   * Función que añade una nota
   * @param note Nota a añadir
   * @param callback Función de callback con error
   */
  public addNote(note: note, callback: (err: Error | null) => void): void {
    if (this.exists(note.title, note.user)) {
      callback(new Error('Note already exists'));
    } else {
      if (!fs.existsSync(this.dir + '/' + note.user)) {
        fs.mkdirSync(this.dir + '/' + note.user);
      }
      fs.writeFileSync(this.dir + '/' + note.user + '/' + note.title + '.json', JSON.stringify(note));
      callback(null);
    }
  }

  /**
   * Función que devuelve todas las notas de un usuario
   * @param user Usuario de las notas
   * @param callback Función de callback con error y notas
   */
  public getNotes(user: string | null, callback: (err: Error | null, notes: note[]) => void): void {
    const dir = this.dir + '/' + user;
    if (!fs.existsSync(dir)) {
      callback(new Error('User not found'), []);
    } else {
      const files = fs.readdirSync(dir, { withFileTypes: true });
      const notes: note[] = [];
      for (const file of files) {
        if (file.isFile()) {
          const note = JSON.parse(fs.readFileSync(dir + '/' + file.name, 'utf8'));
          notes.push(note);
        }
      }
      callback(null, notes);
    }
  }

  /**
   * Función que devuelve una nota
   * @param user Usuario de la nota
   * @param title Título de la nota
   * @param callback Función de callback con error y nota
   */
  public getNote(user: string, title: string, callback: (err: Error | null, note: note | null) => void): void {
    if (!this.exists(title, user)) {
      callback(new Error('Note not found'), null);
      return
    }
    const dir = this.dir + '/' + user;
    const note = JSON.parse(fs.readFileSync(dir + '/' + title + '.json', 'utf8'));
    callback(null, note);
  }

  /**
   * Función que elimina una nota
   * @param user Usuario de la nota
   * @param title Título de la nota
   * @param callback Función de callback con error
   */
  public removeNote(user: string, title: string, callback: (err: Error | null) => void): void {
    if (!this.exists(title, user)) {
      callback(new Error('Note not found'));
    } else {
      const dir = this.dir + '/' + user;
      fs.unlinkSync(dir + '/' + title + '.json');
      callback(null);
    }
  }
}

/**
 * Clase que representa una nota
 * @class
 */
export class note {
  public user: string;
  public title: string;
  public body: string;
  public color: 'red' | 'green' | 'blue' | 'yellow';

  /**
   * Constructor de la clase
   * @param user Usuario de la nota
   * @param title Título de la nota
   * @param body Contenido de la nota
   * @param color Color de la nota
   */
  constructor(user: string, title: string, body: string, color: 'red' | 'green' | 'blue' | 'yellow') {
    this.user = user;
    this.title = title;
    this.body = body;
    this.color = color;
  }
}

