/**
 * Clase que realiza la división de todos los elementos de una lista
 * @class DivMapReduce
 * @extends MapReduce
 */
export class DivMapReduce extends MapReduce {
  /**
   * Constructor de la clase
   * @param numbers Lista de números
   */
